package test2;

public class examqq2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	

	addZ(10,5);	
	addZ(20,4);
	addZ(30,6);
	
}
	
	//overloading	
		
	public static void addZ(int s, int t) {
		System.out.println(s+t);
		
	}
	public static void addZ(int s, int t, int u) {
		System.out.println(s+t+u);
		
		
	}	

	public static void addZ(int s, int t, int u, int v) {
		System.out.println(s+t+u+v);
		
	}

}

